/*global jQuery:false */

// Dependencies: jquery.jcountdown.min.js

// Initialize countdown
(function ($) {
    "use strict";
    
    $(document).ready(function () {
        var $timer = $("#timer");
        
        $timer.html(""); // clear content on start
        
        $timer.countdown({
            "date": $timer.data("to"),
            "offset": $timer.data("offset"),
            "isRTL": $timer.data("rtl")
        });
    });
    
}(jQuery));